import { useRef } from "react";
import { Document, Page } from "react-pdf";

export function StepOne({
  pdfFile,
  setPdfFile,
  field,
  addField,
  updateValue,
  onNext,
}) {
  const pdfRef = useRef(null);

  const handleDrop = (e) => {
    if (!pdfFile || !pdfRef.current) return;

    const pdfRect = pdfRef.current.getBoundingClientRect();

    const x = e.clientX;
    const y = e.clientY;

    // ❌ Drop outside PDF → ignore
    if (
      x < pdfRect.left ||
      x > pdfRect.right ||
      y < pdfRect.top ||
      y > pdfRect.bottom
    ) {
      return;
    }

    // ✅ Drop inside PDF
    addField({
      x: x - pdfRect.left,
      y: y - pdfRect.top,
    });
  };

  return (
    <div style={{ display: "flex", height: "100%" }}>
      {/* PDF COLUMN */}
      <div style={{ flex: 3 }}>
        {pdfFile && (
          <div
            ref={pdfRef}
            style={{ position: "relative", display: "inline-block" }}
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDrop}
          >
            <Document file={pdfFile}>
              <Page pageNumber={1} />
            </Document>

            {field && (
              <input
                value={field.value}
                onChange={(e) => updateValue(e.target.value)}
                style={{
                  position: "absolute",
                  left: field.x,
                  top: field.y,
                }}
              />
            )}
          </div>
        )}
      </div>

      {/* SIDEBAR */}
      <div style={{ flex: 1, padding: 16 }}>
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setPdfFile(e.target.files[0])}
        />

        <input
          draggable
          disabled={!pdfFile}
          placeholder="Drag input"
          style={{ marginTop: 16 }}
        />

        <button
          disabled={!field}
          onClick={onNext}
          style={{ marginTop: 16 }}
        >
          Save & Next
        </button>
      </div>
    </div>
  );
}
