import { Document, Page } from "react-pdf";

export function StepOne({
  pdfFile,
  setPdfFile,
  field,
  addField,
  updateValue,
  onNext,
}) {
  const handleDrop = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    addField({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
    });
  };

  return (
    <div style={{ display: "flex", height: "100%" }}>
      {/* PDF */}
      <div
        style={{ flex: 3, position: "relative" }}
        onDragOver={(e) => e.preventDefault()}
        onDrop={pdfFile && handleDrop}
      >
        {pdfFile && (
          <Document file={pdfFile}>
            <Page pageNumber={1} />
          </Document>
        )}

        {field && (
          <input
            value={field.value}
            onChange={(e) => updateValue(e.target.value)}
            style={{
              position: "absolute",
              left: field.x,
              top: field.y,
            }}
          />
        )}
      </div>

      {/* Sidebar */}
      <div style={{ flex: 1, padding: 16 }}>
        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setPdfFile(e.target.files[0])}
        />

        <input
          draggable
          disabled={!pdfFile || !field}
          placeholder="Drag input"
          style={{ marginTop: 16 }}
        />

        <button
          disabled={!field}
          onClick={onNext}
          style={{ marginTop: 16 }}
        >
          Save & Next
        </button>
      </div>
    </div>
  );
}
